import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Usamos axios directamente
import '../styles/ProductList.css';

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getProducts = async () => {
            setLoading(true); // Asegurarse de que el estado de carga se establezca al inicio
            try {
                // Cambiamos el fetch por una llamada a axios para obtener datos de la API de Pokémon
                const response = await axios.get('https://pokeapi.co/api/v2/pokemon/ditto');
                const product = formatProduct(response.data);
                setProducts([product]); // Ajustamos para manejar un solo producto
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        getProducts();
    }, []);

    const formatProduct = (data) => {
        // Asumimos que quieres formatear la respuesta para que se ajuste a tu estructura de productos
        return {
            id: data.id,
            name: data.name,
            image: data.sprites.front_default, // Usamos la imagen frontal por defecto
            price: 'N/A', // No hay un precio dado, puedes ajustar esto como necesites
            quantity: 'N/A' // Similar al precio, ajustar según necesidad
        };
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (error) {
        return <div className="error">Error: {error}</div>;
    }

    return (
        <div>
            <h1>Pokémon Disponible</h1>
            <div>
                {products.map(product => (
                    <div key={product.id}>
                        <div>
                            <img src={product.image} alt={product.name} className="product-image" />
                            <h2 className="product-id">{product.id}</h2>
                            <h2 className="product-name">{product.name}</h2>
                            <p className="product-price">{product.price}</p>
                            <p className="product-quantity">Stock: {product.quantity}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProductList;
