// src/components/ProductList.js
import React, { useEffect, useState } from 'react';
import { fetchInventory } from '../api';
import '../styles/ProductList.css'; // Importa tu archivo de estilos
import CryptoJS from 'crypto-js';
import axios from 'axios';

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getProducts = async () => {
            try {
                const productos = await fetchInventory();
                const productsWithImages = await addImagesToProducts(productos.listaProductos);
                setProducts(productsWithImages);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        getProducts();
    }, []);

    const addImagesToProducts = async (products) => {
        const ts = 1;
        const publicKey = "b1a9dee7a93fad96acd0d80ddfe720ff";
        const privateKey = "eec1bb4fbdf06949a836d4247e0926add072c8b1";
        const hash = CryptoJS.MD5(ts + privateKey + publicKey).toString();

        const url = `http://gateway.marvel.com/v1/public/comics?ts=${ts}&apikey=${publicKey}&hash=${hash}`;

        try {
            const response = await axios.get(url);
            const comics = response.data.data.results;

            return products.map((product, index) => {
                const comic = comics[index % comics.length]; // Asignar una imagen de cómic de manera circular
                return {
                    ...product,
                    image: `${comic.thumbnail.path}.${comic.thumbnail.extension}`
                };
            });
        } catch (error) {
            console.error('Error fetching images from Marvel API:', error);
            return products;
        }
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (error) {
        return <div className="error">Error: {error}</div>;
    }

    return (
        <div className="product-list-container">
            <h1>Comics Disponibles</h1>
            <div className="product-grid">
                {products.map(product => (
                    <div className="product-card" key={product.id}>
                        <div className="product-details">
                            <img src={product.image} alt={product.name} className="product-image" />
                            <h2 className="product-id">{product.id}</h2>
                            <h2 className="product-name">{product.name}</h2>
                            <p className="product-price">${product.price} USD</p>
                            <p className="product-quantity">Stock: {product.quantity}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProductList;
